$:/plugins/kookma/favorites/styles/main..text:
.tc-tab-buttons + .tc-favorites-hidden {
    display: none;
}
.tc-tab-buttons button.tc-tab-selected {
    fill: {{!!tab-icon-selected-color}};
    stroke: {{!!tab-icon-outline-color}}; 
}
.tc-tab-buttons button:not(.tc-tab-selected) {
    fill: {{!!tab-icon-unselected-color}};
}
.tc-favorites {
    stroke: {{!!button-outline-color}};
    stroke-width:5;
    fill: {{!!button-color}};
}
.tc-image-star-filled {
    height:1em;
    width:1em;
}

$:/plugins/kookma/favorites/styles/style.text:
.sd-input textarea:focus, .sd-input input:focus { outline: none;}
.sd-input textarea, .sd-input input { border:none; border-bottom: 1px dotted gray; margin-left:0px; width:75%;}

.kk-custom-drop-down .tc-drop-down{min-width:60px;}

.kk-favorites svg{
    fill:#aaaaaa;
}
.kk-favorites button:hover svg {
    fill: #000000; 
}

.kk-btn-mini svg { font-size: 0.7em; fill: #bbb; }

/* in folding-editor*/
.fv-folding-button button svg{
	 font-size:0.8em;
	 vertical-align: middle;
}

.fv-folding-button input[type="radio"] {
  width: 0.8em; !important;
  height: 0.8em; !important;
  vertical-align: middle !important;
}
